    <script>
        function confirmDelete(event) {
            event.preventDefault();
            const form = event.target.closest('form');

            if (form !== null) {
                Swal.fire({
                    title: 'Konfirmasi Hapus',
                    text: 'Apakah Anda yakin ingin menghapus item ini?',
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonText: 'Ya',
                    cancelButtonText: 'Tidak',
                    toast: true, // Enable toast style
                    position: 'top-end', // Position the toast
                    timer: 5000, // Optional: Auto-close the toast after 5 seconds
                    showConfirmButton: true, // Option to show buttons
                    timerProgressBar: true // Add progress bar to timer
                }).then((result) => {
                    if (result.isConfirmed) {
                        console.log(form.action);
                        // If the form action exists, proceed with submitting the form
                        form.submit();
                    }
                });
            } else {
                console.log("Form tidak ditemukan");
            }
        }
    </script>

