    @if (!empty($errors->all()))
        <script>
            var err = @json($errors->all());
            var txt = '';
            Object.keys(err).forEach(element => {
                txt += err[element] + '<br>';
            });
            Swal.fire({
                icon: 'error',
                title: 'Error',
                html: txt,
                toast: true, // Enable toast style
                position: 'top-end', // Position the toast
                showConfirmButton: false, // Hide confirm button
                timer: 4000, // Auto-close after 4 seconds
                timerProgressBar: true // Show a progress bar for the timer
            })
        </script>
    @endif

